import React, {useState} from 'react';
import './App.css';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";

let one,setOne;
const isAllowed = (_status,_password) => {
    return (
        (_status==="guest" && _password==="guest") ? "read" :
        (_status==="admin" && _password==="admin") ? "read & write" :
        "nothing"
    );
};
const makeEditable = (_content,_setState) => {
    return (
        <div
            contentEditable='true'
            onInput={e => _setState(e.currentTarget.textContent)}
        >
        {_content}
        </div>
   );
};

const Header = _ => {
    return(
       <div className="header">
            <h1>Hello {_.name} I'm a doc builder!</h1>       
       </div>
    );
};
const Login = _ => {
    return(
        <div className="login">
            {makeEditable("YOURNAME",_.setLogin)}
            {makeEditable("YOURWORD",_.setPassword)}
            You are allowed to: <h3>{isAllowed(_.login,_.password)}</h3>
        </div>
    );
};
const Sidebar = _ => {
    return(
       <div className="sidebar">
       </div>
    );
};
const Content = _ => {
    return(
       <div className="content">
            {_.text}
       </div>
    );
};

const Nav = _ => {
    return(
       <div className="nav">
            {_.isAllowed==="nothing" ? "" : <h6>me nav nav nav nav nav</h6>}
       </div>
    );
};
const dogovors = [
    {route:"/dogovors/one", name:" # M24.0119 ",createdAt:"01-01-2019",createdBy:"Miko",text:"dogtextOne"},
    {route:"/dogovors/two", name:" # M25.0119 ",createdAt:"02-01-2019",createdBy:"Dastin",text:"dogtextTwo"},
    {route:"/dogovors/three", name:" # M26.0119 ",createdAt:"03-01-2019",createdBy:"Judith",text:"dogtextThree"}
];

const Dogovors = _ => {
  const contProps = dogovors.map(e => e.text);
  return (
    <div className="nav">
        {_.isAllowed==="nothing" ? "" : 
            <Router>
              <div className="nav">
                {dogovors.map(e => 
                    <div>
                        <Link to={e.route}>{e.name}</Link>  
                        created at {e.createdAt} by {e.createdBy}
                    </div>
                )}
                <Switch> 
                    {dogovors.map(e => 
                        <Route path={e.route}>
                                <div className="header">{e.name}</div>
                                <div className="wrapper">        
                                    <Sidebar />
                                    <Content {...e}/>
                                </div>
                        </Route>
                    )}        
                </Switch>
              </div>
            </Router>
        }
    </div>
  );
}

export default function App() {
  const [login,setLogin] = useState("");
  const [password,setPassword] = useState("userpassword");
  const loginProps = {login,setLogin,password,setPassword};
  const headProps = {name:login};
  const navProps = {isAllowed:isAllowed(login,password)};
  const dogProps = {isAllowed:isAllowed(login,password)};
  return (
    <div className='wrapper'>
        <Header {...headProps}/>
        <Login {...loginProps}/>
        <Nav {...navProps}/>
        <Dogovors {...dogProps}/>
    </div>
  );
}
