import React, {useState} from 'react';
import './App.css';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";

let one,setOne,two,setTwo,three,setThree;
const isAllowed = (_status,_password) => {
    return (
        (_status==="guest" && _password==="guest") ? "read" :
        (_status==="admin" && _password==="admin") ? "read & write" :
        "nothing"
    );
};
const makeEditable = (_content,_setState) => {
    return (
        <div
            contentEditable='true'
            onInput={e => _setState(e.currentTarget.textContent)}
        >
        {_content}
        </div>
   );
};

const Header = _ => {
    return(
       <div className="header">
            <h1>Hello {_.name} I'm a doc builder!</h1>       
       </div>
    );
};
const Login = _ => {
    return(
        <div className="login">
            {makeEditable("YOURNAME",_.setLogin)}
            {makeEditable("YOURWORD",_.setPassword)}
            You are allowed to: <h3>{isAllowed(_.login,_.password)}</h3>
        </div>
    );
};



const Nav = _ => {
    return(
       <div className="nav">
            {_.isAllowed==="nothing" ? "" : <h6>me nav nav nav nav nav</h6>}
       </div>
    );
};
const dogText = _ => {
    console.log("hello me dogText", "and", _);
    return(
        <div>
            Lorem ipsum dolor sit amet, consectetur adipiscing elit, 
            sed do eiusmod {_[0]} incididunt ut labore et dolore magna 
            aliqua. Ut enim ad minim {_[1]}, quis nostrud exercitation 
            {_[2]} laboris nisi ut aliquip ex ea commodo consequat. 
            Duis aute irure dolor in reprehenderit in {_[1]} velit 
            esse cillum dolore eu fugiat nulla {_[0]}. Excepteur sint 
            occaecat {_[1]} non proident, sunt in culpa qui officia 
            deserunt mollit anim id est laborum.
        </div>
    );
};
const Content = _ => {
    console.log("cinetnt receives", _);
    return(
       <div className="content">
            {_.text}
       </div>
    );
};
//const dogVaies = () => {};
const dogVars = [
    [one,two,three],
    ["FOUR","FIVE","SIX"],
    ["SEVEN","EIGHT","NINE"]
];
const dogovors = [
    //{route:"/dogovors/one", name:" # M24.0119 ",createdAt:"01-01-2019",createdBy:"Miko",text:dogText(dogVars[0])},
    {route:"/dogovors/one", name:" # M24.0119 ",createdAt:"01-01-2019",createdBy:"Miko",text:dogText(dogVars[0]), vs:dogVars[0] },
    {route:"/dogovors/two", name:" # M25.0119 ",createdAt:"02-01-2019",createdBy:"Dastin",text:dogText(dogVars[1]), vs:dogVars[1] },
    {route:"/dogovors/three", name:" # M26.0119 ",createdAt:"03-01-2019",createdBy:"Judith",text:dogText(dogVars[2]), vs:dogVars[2] }
];
const Sidebar = _ => {
    return(
       <div className="sidebar">
        Variables: 
            {_.vs.map(e => <div>{makeEditable(e,setOne)}</div>)}          
       </div>
    );
};
const Dogovors = _ => {
  //const contProps = {..._};
  console.log("dog receives", _);
  return (
    <div className="nav">
        {_.isAllowed==="nothing" ? "" :
            <Router>
              <div className="nav">
                {dogovors.map(e => 
                    <div>
                        <Link to={e.route}>{e.name}</Link>  
                        created at {e.createdAt} by {e.createdBy}
                    </div>
                )}
                <Switch> 
                    {dogovors.map((e,i) => 
                        <Route path={e.route}>
                                <div className="header">{e.name}</div>
                                <div className="wrapper">        
                                    <Sidebar {...e}/>
                                    <Content {..._}/>
                                </div>
                        </Route>
                    )}        
                </Switch>
              </div>
            </Router> 
        }
    </div>
  );
}

export default function App() {
  const [login,setLogin] = useState("");
  const [password,setPassword] = useState("userpassword");
  [one,setOne] = useState("ONE");
  [two,setTwo] = useState("TWO");
  [three,setThree] = useState("THREE");
  const [four,setFour] = useState("FOUR");
  const [five,setFive] = useState("FIVE");
  const [six,setSix] = useState("SIX");
  const [seven,setSeven] = useState("SEVEN");
  const [eight,setEight] = useState("EIGHT");
  const [nine,setNine] = useState("NINE");
  const loginProps = {login,setLogin,password,setPassword};
  const headProps = {name:login};
  const navProps = {isAllowed:isAllowed(login,password)};
  const dogProps = {isAllowed:isAllowed(login,password), stateVars:[[one,two,three],[four,five,six],[seven,eight,nine]]};
  console.log([[one,two,three],[four,five,six],[seven,eight,nine]]);
  return (
    <div className='wrapper'>
        <Header {...headProps}/>
        <Login {...loginProps}/>
        <Nav {...navProps}/>
        <Dogovors {...dogProps}/>
    </div>
  );
}
