import React, {useState} from 'react';
import './App.css';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";

let one,setOne,two,setTwo,three,setThree;
const isAllowed = (_status,_password) => {
    return (
        (_status==="guest" && _password==="guest") ? "read" :
        (_status==="admin" && _password==="admin") ? "read & write" :
        "nothing"
    );
};
const makeEditable = (_content,_setState) => {
    return (
        <div
            contentEditable='true'
            onInput={e => _setState(e.currentTarget.textContent)}
        >
        {_content}
        </div>
   );
};

const Header = _ => {
    return(
       <div className="header">
            <h1>Hello {_.name} I'm a doc builder!</h1>       
       </div>
    );
};
const Login = _ => {
    return(
        <div className="login">
            {makeEditable("YOURNAME",_.setLogin)}
            {makeEditable("YOURWORD",_.setPassword)}
            You are allowed to: <h3>{isAllowed(_.login,_.password)}</h3>
        </div>
    );
};



const Nav = _ => {
    return(
       <div className="nav">
            {_.isAllowed==="nothing" ? "" : <h6>me nav nav nav nav nav</h6>}
       </div>
    );
};

const dogovors = [
    {route:"/dogovors/one", name:" # M24.0119 ",createdAt:"01-01-2019",createdBy:"Miko"},
    {route:"/dogovors/two", name:" # M25.0119 ",createdAt:"02-01-2019",createdBy:"Dastin"},
    {route:"/dogovors/three", name:" # M26.0119 ",createdAt:"03-01-2019",createdBy:"Judith"}
];

export default function App() {
  const [login,setLogin] = useState("");
  const [password,setPassword] = useState("userpassword");
  const [one,setOne] = useState("ONE");
  const loginProps = {login,setLogin,password,setPassword};
  const headProps = {name:login};
  const navProps = {isAllowed:isAllowed(login,password)};

  return (
    <div className='wrapper'>
        <Header {...headProps}/>
        <Login {...loginProps}/>
        <Nav {...navProps}/>
        {<div>
            <div className="sidebar">VARIABLES:</div>
            <div className="content">
                Lorem ipsum dolor sit amet, consectetur adipiscing elit, 
                sed do eiusmod {one} incididunt ut labore et dolore magna 
                aliqua. Ut enim ad minim {one}, quis nostrud exercitation 
                {one} laboris nisi ut aliquip ex ea commodo consequat. 
                Duis aute irure dolor in reprehenderit in {one} velit 
                esse cillum dolore eu fugiat nulla {one}. Excepteur sint 
                occaecat {one} non proident, sunt in culpa qui officia 
                deserunt mollit anim id est laborum.
            </div>
        </div>
        }

    </div>
  );
}
