import React, {useState} from 'react';
import './App.css';

let login,setLogin,password,setPassword,one,setOne;
const isAllowed = (_status,_password) => {
    return (
        (_status==="guest" && _password==="guest") ? "read" :
        (_status==="admin" && _password==="admin") ? "read & write" :
        "nothing"
    );
};

const Header = () => {
    return(
       <div className="header">
            {/*<h6>me header</h6>*/}
            <h1>Hello I'm a doc builder!</h1>

       </div>
    );
};
const Login = () => {
    [logname,setLogname] = useState("");
    [logword,setLogword] = useState("");
    return(
         <div className="login">
        {/*<h6>me login</h6>*/}
        <div clasName="login">
            {makeEditable("YOURNAME",setLogname)}
            {makeEditable("YOURWORD",setLogword)}
            You are allowed to: {isAllowed(logname,logword)}
        </div>
        );
    };
       </div>
    );
};
const Sidebar = () => {
    return(
       <div className="sidebar">
            Hello me sidebar<br/>
            Hello me sidebar<br/>
            Hello me sidebar
       </div>
    );
};
const Content = () => {
    return(
       <div className="content">
            <h6>me content</h6>
       </div>
    );
};

const Nav = () => {
    return(
       <div className="nav">
            <h6>me nav nav nav nav nav</h6>
       </div>
    );
};

export default function App() {
  return (
    <div className='wrapper'>
        <Header />
        <Login />
        <Sidebar />
        <Nav />
        <Content />
    </div>
  );
}
