// COMPONENTS ----------------------------------------------------------------------------------------------

    const Head = () => {
        [logname,setLogname] = useState("");
        [logword,setLogword] = useState("");
        const isAllowed = (_status,_password) => {
            return (
                (_status==="guest" && _password==="guest") ? "read" :
                (_status==="admin" && _password==="admin") ? "read & write" :
            "nothing"
            );
        };
        return (
            <div>
             <div clasName="login">
                    {makeEditable("YOURNAME",setLogname)}
                    {makeEditable("YOURWORD",setLogword)}
                    You are allowed to: {isAllowed(logname,logword)}
            </div>
            <div className="header">
                <h1>Hello {logname} I'm a doc builder! </h1>
            </div>
            </div>
        );
    };
    const Sidebar = () => {
        return(
           <div className="sidebar">
            <div className="varnav">
                        VARIABLES:<br/>
                        {makeEditable("ONE",setOne)}
                    </div> 
           </div> 
        );
    };
    const Content = () => {
        return(
            <div className="content">        
            <ImmediateInput {...setFour} />


            </div>
        );
    };


export default function App() {

  [one,setOne] = useState('ONE'); 
  return (
    <Router>

        <Switch>         
          <Route path="/dogovors">
            <Dogovors />
          </Route>
          <Route path="/">
            <Home />
          </Route>
        </Switch>
    </Router>
  );
}

function Home() {
     const isAllowed = (_status,_password) => {
            return (
                (_status==="guest" && _password==="guest") ? "read" :
                (_status==="admin" && _password==="admin") ? "read & write" :
            "nothing"
            );
        };

      return (
        <div>

            <div className="wrapper">
                <Head />           

            <div className="content">
            {isAllowed(logname,logword)==="read" ?
                 <div>
                       <Sidebar /> 
                    <div className="content">
                        Lorem ipsum dolor sit amet, consectetur adipiscing elit, 
                        sed do eiusmod {one} incididunt ut labore et dolore magna 
                        aliqua. Ut enim ad minim {one}, quis nostrud exercitation 
                        {one} laboris nisi ut aliquip ex ea commodo consequat. 
                        Duis aute irure dolor in reprehenderit in {one} velit 
                        esse cillum dolore eu fugiat nulla {one}. Excepteur sint 
                        occaecat {one} non proident, sunt in culpa qui officia 
                        deserunt mollit anim id est laborum.
                    </div>
                </div>
             : "otstoy"
            }
            </div>
            </div>

        </div>
      );
}

const dogovors = [
    {name:" # M24.0119 ",createdAt:"01-01-2019",createdBy:"Miko"},
    {name:" # M25.0119 ",createdAt:"02-01-2019",createdBy:"Dastin"},
    {name:" # M26.0119 ",createdAt:"03-01-2019",createdBy:"Judith"}
];

const Dogovors = () => {
  return (
        <Router>
      <div className="nav">
        <div>
            <Link to="/dogovors/one">{dogovors[0].name}</Link>  
            created at {dogovors[0].createdAt} by {dogovors[0].createdBy}
        </div>
        <div>
            <Link to="/dogovors/one">{dogovors[1].name}</Link>  
            created at {dogovors[1].createdAt} by {dogovors[1].createdBy}
        </div>
       <div>
            <Link to="/dogovors/one">{dogovors[2].name}</Link>  
            created at {dogovors[2].createdAt} by {dogovors[2].createdBy}
        </div>

        <Switch>         
          <Route path="/dogovors/one">
            <One />
          </Route>
          <Route path="/dogovors/two">
            <Two />
          </Route>
          <Route path="/dogovors/three">
            <Three />
          </Route>
        </Switch>

      </div>
    </Router>
        );
}

function One() {
