import React, {useState} from 'react';
import './App.css';

let one,setOne;
const isAllowed = (_status,_password) => {
    return (
        (_status==="guest" && _password==="guest") ? "read" :
        (_status==="admin" && _password==="admin") ? "read & write" :
        "nothing"
    );
};
const makeEditable = (_content,_setState) => {
    return (
        <div
            contentEditable='true'
            onInput={e => _setState(e.currentTarget.textContent)}
        >
        {_content}
        </div>
   );
};

const Header = _ => {
    return(
       <div className="header">
            <h1>Hello {_.name} I'm a doc builder!</h1>       
       </div>
    );
};
const Login = _ => {
    return(
        <div className="login">
            {makeEditable("YOURNAME",_.setLogin)}
            {makeEditable("YOURWORD",_.setPassword)}
            You are allowed to: <h3>{isAllowed(_.login,_.password)}</h3>
        </div>
    );
};
const Sidebar = () => {
    return(
       <div className="sidebar">
            Hello me sidebar<br/>
            Hello me sidebar<br/>
            Hello me sidebar
       </div>
    );
};
const Content = () => {
    return(
       <div className="content">
            <h6>me content</h6>
       </div>
    );
};

const Nav = () => {
    return(
       <div className="nav">
            <h6>me nav nav nav nav nav</h6>
       </div>
    );
};
const Main = () => {
    return(
        <div>
            <Sidebar />
                <Nav />
                <Content />
        </div>
    );
};

export default function App() {
  const [login,setLogin] = useState("");
  const [password,setPassword] = useState("userpassword");
  const loginProps = {login,setLogin,password,setPassword};
  const headProps = {name:login}
  return (
    <div className='wrapper'>
        <Header {...headProps}/>
        <Login {...loginProps}/>
        <Sidebar />
        <Nav />
        <Content />
     </div>
  );
}
