import React, {useState} from 'react';
import './App.css';

let login,setLogin,password,setPassword,one,setOne;
const isAllowed = (_status,_password) => {
    return (
        (_status==="guest" && _password==="guest") ? "read" :
        (_status==="admin" && _password==="admin") ? "read & write" :
        "nothing"
    );
};
const makeEditable = (_content,_setState) => {
    return (
        <div
            contentEditable='true'
            onInput={e => _setState(e.currentTarget.textContent)}
        >
        {_content}
        </div>
   );
};

const Header = () => {
    return(
       <div className="header">
            {/*<h6>me header</h6>*/}
            <h1>Hello I'm a doc builder!</h1>

       </div>
    );
};
const Login = () => {
    [login,setLogin] = useState("");
    [password,setPassword] = useState("");
    return(
         <div className="login">
        {/*<h6>me login</h6>*/}
        <div clasName="login">
            {makeEditable("YOURNAME",setLogin)}
            {makeEditable("YOURWORD",setPassword)}
            You are allowed to: {isAllowed(login,password)}
        </div>
        );
    };
       </div>
    );
};
const Sidebar = () => {
    return(
       <div className="sidebar">
            Hello me sidebar<br/>
            Hello me sidebar<br/>
            Hello me sidebar
       </div>
    );
};
const Content = () => {
    return(
       <div className="content">
            <h6>me content</h6>
       </div>
    );
};

const Nav = () => {
    return(
       <div className="nav">
            <h6>me nav nav nav nav nav</h6>
       </div>
    );
};

export default function App() {
  return (
    <div className='wrapper'>
        <Header />
        <Login />
        <Sidebar />
        <Nav />
        <Content />
    </div>
  );
}
