import React, {useState} from 'react';
//import logo from './logo.svg';
import './App.css';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";

let logname,logword,setLogname,setLogword,one,setOne;


      const assert = (_statement,_function,_positiveOutcome) => {
        return(
                JSON.stringify(_function) === JSON.stringify(_positiveOutcome) 
                ? console.log(`YES! ${_statement}, ooph!`) 
                : console.log(`NOPE! ${_statement} works WRONG!!!`)
        );
    };

//  singleWrapper --------------------------------------------------------------------------

    const SingleWrapper = _input => <div>{_input}</div>;
    const makeEditable = (_content,_setState) => {
        return (
            <div
                contentEditable='true'
                onInput={e => _setState(e.currentTarget.textContent)}
            >
            {_content}
            </div>
       );
    };
    const ImmediateInput = _x => {
        return (
            <div>
                Lorem ipsum dolor sit amet, consectetur adipiscing elit, 
                sed do eiusmod {_x.one } incididunt ut labore et dolore magna 
                aliqua. Ut enim ad minim {_x.four}, quis nostrud exercitation 
                {_x.two} laboris nisi ut aliquip ex ea commodo consequat. 
                Duis aute irure dolor in reprehenderit in {_x.one} velit 
                esse cillum dolore eu fugiat nulla {_x.three}. Excepteur sint 
                occaecat {_x.one} non proident, sunt in culpa qui officia 
                deserunt mollit anim id est laborum.
            </div>        
        );        
    };
    const simpleEdit = _x => <span contentEditable = "true" type = "text">{_x}</span>;
    const setFour = {
        one: simpleEdit("ONE"),
    };
    const SideBarInput = _x => {
        return (
              <div>
                varibles:<br/>
                <div>{_x.one}</div>
              </div>
        );        
    };


// COMPONENTS ----------------------------------------------------------------------------------------------

    const Head = () => {
        [logname,setLogname] = useState("");
        [logword,setLogword] = useState("");
        const isAllowed = (_status,_password) => {
            return (
                (_status==="guest" && _password==="guest") ? "read" :
                (_status==="admin" && _password==="admin") ? "read & write" :
            "nothing"
            );
        };
        return (
            <div className="wrapper">
             <div clasName="login">
                    {makeEditable("YOURNAME",setLogname)}
                    {makeEditable("YOURWORD",setLogword)}
                    You are allowed to: {isAllowed(logname,logword)}
            </div>
            <div className="header header">
                <h1>Hello {logname} I'm a doc builder! </h1>
            </div>
            </div>
        );
    };
    const Sidebar = () => {
        return(
           <div className="sidebar">
            <div className="varnav">
                        VARIABLES:<br/>
                        {makeEditable("ONE",setOne)}
                    </div> 
           </div> 
        );
    };
    const Content = () => {
        return(
            <div className="content">        
            <ImmediateInput {...setFour} />


            </div>
        );
    };


export default function App() {

  [one,setOne] = useState('ONE'); 
  return (
    <Router>

        <Switch>         
          <Route path="/">
            <Home />
          </Route>
        </Switch>
    </Router>
  );
}

function Home() {
     const isAllowed = (_status,_password) => {
            return (
                (_status==="guest" && _password==="guest") ? "read" :
                (_status==="admin" && _password==="admin") ? "read & write" :
            "nothing"
            );
        };

      return (
        <div>

            <div className="wrapper">
                <Head />           

            <div className="content">
            {isAllowed(logname,logword)==="read" ?
                 <div>
                       <Sidebar /> 
                    <div className="content">
                        Lorem ipsum dolor sit amet, consectetur adipiscing elit, 
                        sed do eiusmod {one} incididunt ut labore et dolore magna 
                        aliqua. Ut enim ad minim {one}, quis nostrud exercitation 
                        {one} laboris nisi ut aliquip ex ea commodo consequat. 
                        Duis aute irure dolor in reprehenderit in {one} velit 
                        esse cillum dolore eu fugiat nulla {one}. Excepteur sint 
                        occaecat {one} non proident, sunt in culpa qui officia 
                        deserunt mollit anim id est laborum.
                    </div>
                </div>
             : "otstoy"
            }
            </div>
            </div>

        </div>
      );
}
