import React, {useState} from 'react';
import './App.css';

let one,setOne;
const isAllowed = (_status,_password) => {
    return (
        (_status==="guest" && _password==="guest") ? "read" :
        (_status==="admin" && _password==="admin") ? "read & write" :
        "nothing"
    );
};
const makeEditable = (_content,_setState) => {
    return (
        <div
            contentEditable='true'
            onInput={e => _setState(e.currentTarget.textContent)}
        >
        {_content}
        </div>
   );
};

const Header = _name => {
    return(
       <div className="header">
            {/*<h6>me header</h6>*/}
            Hello {_name} I'm a doc builder!

       </div>
    );
};
const Login = _ => {
    return(
        <div className="login">
            {makeEditable("YOURNAME",_.setLogin)}
            {makeEditable("YOURWORD",_.setPassword)}
            You are allowed to: {isAllowed(_.login,_.password)}
        </div>
    );
};
const Sidebar = () => {
    return(
       <div className="sidebar">
            Hello me sidebar<br/>
            Hello me sidebar<br/>
            Hello me sidebar
       </div>
    );
};
const Content = () => {
    return(
       <div className="content">
            <h6>me content</h6>
       </div>
    );
};

const Nav = () => {
    return(
       <div className="nav">
            <h6>me nav nav nav nav nav</h6>
       </div>
    );
};

export default function App() {
  const [login,setLogin] = useState("username");
  const [password,setPassword] = useState("userpassword");
  const loginProps = {login,setLogin,password,setPassword};
  return (
    <div className='wrapper'>
        <Header name={login}/>
        <Login {...loginProps}/>
        <Sidebar />
        <Nav />
        <Content />
    </div>
  );
}
